head.script:
function init_matrix() {
  c = document.createElementNS("http://www.w3.org/2000/svg", "circle");
  c.setAttribute("cx", "5");
  c.setAttribute("cy", "5");
  c.setAttribute("r", "5");
  c.setAttribute("stroke", "red");
  c.setAttribute("fill", "white");
  svg = document.getElementById('SVG');
  svg.appendChild(c);
}
